function App() {
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleChange = (questionId, selectedOptionType) => {
    setAnswers({
      ...answers,
      [questionId]: selectedOptionType
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const scores = { Ant: 0, Owl: 0, Bear: 0, Dolphin: 0, Squirrel: 0, Fox: 0 };
    for (let qId in answers) {
      const type = answers[qId];
      scores[type] = (scores[type] || 0) + 1;
    }
    let highestType = null;
    let highestScore = -1;
    for (let type in scores) {
      if (scores[type] > highestScore) {
        highestScore = scores[type];
        highestType = type;
      }
    }
    setResult(quizResultDescriptions[highestType]);
  };

  return (
    <div className="App">
      <LanguageSwitcher />
      <h1>Discover Your Money Management Style!</h1>
      <p>
        Take this fun quiz to uncover your unique financial personality through a series of engaging questions.
      </p>
      <form onSubmit={handleSubmit}>
        {questions.map((q) => (
          <div key={q.id} className="question-block">
            <h2>{`Question ${q.id}`}</h2>
            <p>{q.question}</p>
            {q.options.map((option, index) => (
              <div key={index} className="option">
                <label>
                  <input
                    type="radio"
                    name={`question-${q.id}`}
                    value={option.type}
                    onChange={() => handleChange(q.id, option.type)}
                    required
                  />
                  {option.option}
                </label>
              </div>
            ))}
          </div>
        ))}
        <button type="submit" className="submit-button">Submit Quiz</button>
      </form>
      {result && (
        <div className="result">
          <h2>{result.title}</h2>
          <p><strong>Result Analysis:</strong> {result.analysis}</p>
          <p><strong>Advice:</strong> {result.advice}</p>
        </div>
      )}
    </div>
  );
}

export default App;
