import React, { useState } from 'react';
import { questions, quizResultDescriptions } from './data';
import { useNavigate } from 'react-router-dom';
import './App.css';

const QuizPage = () => {
  const navigate = useNavigate();
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  // answers 用于存储每题选中的选项（key 为 question id）
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);
  const [isCountingDown, setIsCountingDown] = useState(false);
  const [countdown, setCountdown] = useState(3);

  const handleChange = (questionId, selectedOptionIndex) => {
    setAnswers({
      ...answers,
      [questionId]: selectedOptionIndex,
    });
  };

  const goToNextQuestion = () => {
    if (currentQuestionIndex + 2 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 2);
    }
  };

  const goToPreviousQuestion = () => {
    if (currentQuestionIndex - 2 >= 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 2);
    }
  };

  const startCountdown = (finalResult) => {
    setIsCountingDown(true);
    setCountdown(3);
    const interval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          setIsCountingDown(false);
          setResult(finalResult);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 初始化每种动物的得分
    const scores = {
      Squirrel: 0,
      Penguin: 0,
      Owl: 0,
      Fox: 0,
      Raccoon: 0,
      Cheetah: 0,
      "Golden Retriever": 0,
      Capybara: 0,
    };

    // 遍历每题并累加对应选项的得分
    questions.forEach((question) => {
      const answerIndex = answers[question.id];
      if (answerIndex !== undefined) {
        const selectedOption = question.options[answerIndex];
        for (let animal in selectedOption.score) {
          scores[animal] += selectedOption.score[animal];
        }
      }
    });

    // 取出最高分对应的动物
    const maxScore = Math.max(...Object.values(scores));
    const topAnimals = Object.keys(scores).filter(
      (animal) => scores[animal] === maxScore
    );

    let finalResult;
    if (topAnimals.length === 1) {
      finalResult = quizResultDescriptions[topAnimals[0]];
    } else {
      // 如果存在平局，则生成混合人格结果
      const hybridKey = topAnimals.join(" x ");
      finalResult = {
        title: `Hybrid Personality: ${hybridKey}`,
        intro:
          "You embody a mix of financial qualities that make you uniquely versatile. Embrace the best traits from each side of your personality!",
        strengths: [
          "Unique blend of strengths from: " + topAnimals.join(", "),
        ],
        weaknesses: [
          "A combined set of challenges might slow down your decision making.",
        ],
        waysToImprove: [
          "Focus on leveraging your combined strengths to optimize your financial strategy, and remember: a little balance goes a long way.",
        ],
      };
    }

    // 开启倒计时动画后显示结果
    startCountdown(finalResult);
  };

  // 重做测验，重置状态
  const handleRetake = () => {
    setCurrentQuestionIndex(0);
    setAnswers({});
    setResult(null);
  };

  // 跳转到金融人格详情页
  const handleLearnMore = () => {
    navigate('/personality');
  };

  // 每页显示两题
  const currentQuestions = questions.slice(currentQuestionIndex, currentQuestionIndex + 2);

  return (
    <div className="quiz-page">
      <div className="quiz-header">
        <h2>
          Ultimate Financial Personality Quiz – The Internet Animal Edition
        </h2>
        <p>
          Answer a few fun questions to reveal your unique financial habits in the most brutally honest way possible.
        </p>
      </div>

      {/* 仅在未生成结果时显示测验表单 */}
      {!result && (
        <form onSubmit={handleSubmit}>
          <div className="question-pair">
            {currentQuestions.map((question, idx) => (
              <div className="question-card" key={question.id}>
                <div className="question-number-container">
                  <div className="circle-background"></div>
                  <div className="circle-border"></div>
                  <div className="question-number-text">
                    #{currentQuestionIndex + idx + 1}
                  </div>
                </div>
                <div className="question-text">
                  {question.question}
                </div>
                <div className="options-grid">
                  {question.options.map((option, index) => (
                    <label key={index} className="option-label">
                      <input
                        type="radio"
                        name={`question-${question.id}`}
                        value={index}
                        checked={answers[question.id] === index}
                        onChange={() => handleChange(question.id, index)}
                        required
                      />
                      <div className="option-box">
                        {option.option}
                      </div>
                    </label>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="nav-buttons">
            {currentQuestionIndex > 0 && (
              <button type="button" className="nav-btn" onClick={goToPreviousQuestion}>
                ← Previous
              </button>
            )}
            {currentQuestionIndex + 2 < questions.length ? (
              <button type="button" className="nav-btn" onClick={goToNextQuestion}>
                Next →
              </button>
            ) : (
              <button type="submit" className="submit-btn">
                Submit Quiz
              </button>
            )}
          </div>
        </form>
      )}

      {isCountingDown && (
        <div className="countdown-overlay">
          <div className="countdown-modal">
            <h3>Waiting for your result...</h3>
            <div className="countdown-number">{countdown}</div>
          </div>
        </div>
      )}

      {result && !isCountingDown && (
        <div className="result-container" style={{ textAlign: 'left' }}>
          <h3>{result.title}</h3>
          <p>{result.intro}</p>
          <div className="result-section">
            <h4>Strengths:</h4>
            <div className="result-text">
              {result.strengths.map((strength, i) => (
                <p key={i}>{strength}</p>
              ))}
            </div>
          </div>
          <div className="result-section">
            <h4>Weaknesses:</h4>
            <div className="result-text">
              {result.weaknesses.map((weakness, i) => (
                <p key={i}>{weakness}</p>
              ))}
            </div>
          </div>
          <div className="result-section">
            <h4>Ways to Improve:</h4>
            <div className="result-text">
              {result.waysToImprove.map((tip, i) => (
                <p key={i}>{tip}</p>
              ))}
            </div>
          </div>
          <div className="post-result-buttons" style={{ marginTop: '20px' }}>
            <button type="button" className="nav-btn" style={{ marginRight: '15px' }} onClick={handleRetake}>
              Retake the Quiz
            </button>
            <button type="button" className="nav-btn" onClick={handleLearnMore}>
              Learn More Financial Personality
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default QuizPage;
